import React, {useState,Component} from "react";
import StylesCard from  './Card.module.scss'

class Card extends Component
{
    // eslint-disable-next-line no-useless-constructor
    check = false
    constructor(props) {
        super(props);
    }
    tovar = this.props.tovar
    render() {
        const clicked = (props) => {
            check = props.onAdd.isAdded;
        }
        return (
            <div className={StylesCard.card}>
                <div className={StylesCard.selected}>
                    <img src="/img/like.svg" alt='liked'/>
                </div>
                <img width={133} height={112} src={this.tovar.img} alt='sneacker'/>
                <h5>{this.tovar.name}</h5>
                <div className='d-flex fle justify-between align-center'>
                    <div className='d-flex flex-column'>
                        <span>Цена:</span>
                        <b>{this.tovar.price} руб.</b>
                    </div>
                    <img className={StylesCard.plus} onClick={clicked} src={check ? "/img/checked-btn.png" : "/img/btn.png"} alt='btn'/>
                </div>
            </div>
        );
    }
}


export default Card;
